 $Users = Import-Csv -Path "C:\users.csv"foreach ($User in $Users){    $Displayname = $User.firstname + " " + $User.lastname    $UserFirstname = $User.firstname    $UserLastname = $User.lastname    $OU = "ou=" + $User.department + ",dc=dom50,dc=com"    #$SAM = $User.firstname + " " + $User.lastname    #$UPN = $User.Firstname + "." + $User.Lastname + "@" + $User.Maildomain    $UPN = $User.upn    #$Description = $User.Description    $Password = "test12345="    New-ADUser -Name "$Displayname" -DisplayName "$Displayname" -GivenName "$UserFirstname" -Surname "$UserLastname" -AccountPassword (ConvertTo-SecureString $Password -AsPlainText -Force) -Enabled $false -Path "$OU" -ChangePasswordAtLogon $false –PasswordNeverExpires $true}